    public async override Task<object> fetchOHLCV(object symbol, object timeframe = null, object since = null, object limit = null, object parameters = null)
    {
        /**
        * @method
        * @name bitir#fetchOHLCV
        * @description fetches historical candlestick data containing the open, high, low, and close price, and the volume of a market
        * @see https://www.bit.ir/fa
        * @param {string} symbol unified symbol of the market to fetch OHLCV data for
        * @param {string} timeframe the length of time each candle represents
        * @param {int} [since] timestamp in ms of the earliest candle to fetch
        * @param {int} [limit] the maximum amount of candles to fetch
        * @param {object} [params] extra parameters specific to the exchange API endpoint
        * @returns {int[][]} A list of candles ordered as timestamp, open, high, low, close, volume
        */
        timeframe ??= "1h";
        parameters ??= new Dictionary<string, object>();
        await this.loadMarkets();
        object market = this.market(symbol);
        symbol = add(getValue(market, "base"), getValue(market, "quote"));
        if (isTrue(isEqual(getValue(market, "quote"), "IRT")))
        {
            symbol = add(getValue(market, "base"), "IRR");
        }
        object endTime = (new DateTimeOffset(DateTime.UtcNow)).ToUnixTimeMilliseconds();
        object request = new Dictionary<string, object>() {
            { "symbol", symbol },
            { "from", subtract((divide(endTime, 1000)), (multiply(multiply(24, 60), 60))) },
            { "to", divide(endTime, 1000) },
            { "resolution", this.safeString(this.timeframes, timeframe, timeframe) },
        };
        if (isTrue(!isEqual(since, null)))
        {
            ((IDictionary<string,object>)request)["from"] = divide(since, 1000);
        }
        ((IDictionary<string,object>)request)["from"] = this.safeInteger(request, "from");
        ((IDictionary<string,object>)request)["to"] = this.safeInteger(request, "to");
        if (isTrue(!isEqual(timeframe, null)))
        {
            ((IDictionary<string,object>)request)["resolution"] = this.safeString(this.timeframes, timeframe, timeframe);
        }
        object response = await this.publicGetV2UdfRealHistory(request);
        object openList = this.safeValue(response, "o", new List<object>() {});
        object highList = this.safeList(response, "h", new List<object>() {});
        object lowList = this.safeList(response, "l", new List<object>() {});
        object closeList = this.safeList(response, "c", new List<object>() {});
        object volumeList = this.safeList(response, "v", new List<object>() {});
        object timestampList = this.safeList(response, "t", new List<object>() {});
        object ohlcvs = new List<object>() {};
        for (object i = 0; isLessThan(i, getArrayLength(openList)); postFixIncrement(ref i))
        {
            if (isTrue(isEqual(getValue(market, "quote"), "IRT")))
            {
                ((List<object>)openList)[Convert.ToInt32(i)] = ((bool) isTrue(getValue(openList, i))) ? divide(getValue(openList, i), 10) : 0;
                ((List<object>)highList)[Convert.ToInt32(i)] = ((bool) isTrue(getValue(highList, i))) ? divide(getValue(highList, i), 10) : 0;
                ((List<object>)lowList)[Convert.ToInt32(i)] = ((bool) isTrue(getValue(lowList, i))) ? divide(getValue(lowList, i), 10) : 0;
                ((List<object>)closeList)[Convert.ToInt32(i)] = ((bool) isTrue(getValue(closeList, i))) ? divide(getValue(closeList, i), 10) : 0;
                ((List<object>)volumeList)[Convert.ToInt32(i)] = ((bool) isTrue(getValue(volumeList, i))) ? divide(getValue(volumeList, i), 10) : 0;
            }
            ((IList<object>)ohlcvs).Add(new List<object>() {getValue(timestampList, i), getValue(openList, i), getValue(highList, i), getValue(lowList, i), getValue(closeList, i), getValue(volumeList, i)});
        }
        return this.parseOHLCVs(ohlcvs, market, timeframe, since, limit);
    }

    public async override Task<object> fetchOrderBook(object symbol, object limit = null, object parameters = null)
    {
        /**
        * @method
        * @name bitir#fetchOrderBooks
        * @description fetches information on open orders with bid (buy) and ask (sell) prices, volumes and other data for multiple markets
        * @see https://www.bit.ir/fa
        * @param {string[]|undefined} symbols list of unified market symbols, all symbols fetched if undefined, default is undefined
        * @param {int} [limit] max number of entries per orderbook to return, default is undefined
        * @param {object} [params] extra parameters specific to the exchange API endpoint
        * @returns {object} a dictionary of [order book structures]{@link https://docs.ccxt.com/#/?id=order-book-structure} indexed by market symbol
        */
        parameters ??= new Dictionary<string, object>();
        await this.loadMarkets();
        object market = this.market(symbol);
        object request = new Dictionary<string, object>() {
            { "id", getValue(market, "id") },
        };
        object response = await this.publicGetV1MarketIdOrder(request);
        object orderbookList = this.safeList(response, "data");
        object orberbook = new Dictionary<string, object>() {
            { "asks", new List<object>() {} },
            { "bids", new List<object>() {} },
        };
        for (object i = 0; isLessThan(i, getArrayLength(orderbookList)); postFixIncrement(ref i))
        {
            object orderType = this.safeString(getValue(orderbookList, i), "type");
            object price = this.safeFloat(getValue(orderbookList, i), "price", 0);
            object amount = this.safeFloat(getValue(orderbookList, i), "amount", 0);
            if (isTrue(isEqual(orderType, "sell")))
            {
                if (isTrue(isEqual(getValue(market, "quote"), "IRT")))
                {
                    price = divide(price, 10);
                }
                ((IList<object>)getValue(orberbook, "asks")).Add(new List<object>() {price, amount});
            }
            if (isTrue(isEqual(orderType, "buy")))
            {
                if (isTrue(isEqual(getValue(market, "quote"), "IRT")))
                {
                    price = divide(price, 10);
                }
                ((IList<object>)getValue(orberbook, "bids")).Add(new List<object>() {price, amount});
            }
        }
        object timestamp = (new DateTimeOffset(DateTime.UtcNow)).ToUnixTimeMilliseconds();
        return this.parseOrderBook(orberbook, symbol, timestamp);
    }

    public override object sign(object path, object api = null, object method = null, object parameters = null, object headers = null, object body = null)
    {
        api ??= "public";
        method ??= "GET";
        parameters ??= new Dictionary<string, object>();
        object query = this.omit(parameters, this.extractParams(path));
        object url = add(add(getValue(getValue(this.urls, "api"), "public"), "/"), path);
        if (isTrue(isTrue(isEqual(path, "v1/market")) && isTrue(!isEqual(getValue(parameters, "id"), null))))
        {
            url = add(add(url, "/"), getValue(parameters, "id"));
        }
        if (isTrue(isEqual(path, "v2/udf/real/history")))
        {
            url = add(add(url, "?"), this.urlencode(query));
        }
        if (isTrue(isEqual(path, "v1/market/{id}/order")))
        {
            url = add(add(add(getValue(getValue(this.urls, "api"), "public"), "/v1/market/"), getValue(parameters, "id")), "/order");
        }
        headers = new Dictionary<string, object>() {
            { "Content-Type", "application/json" },
        };
        return new Dictionary<string, object>() {
            { "url", url },
            { "method", method },
            { "body", body },
            { "headers", headers },
        };
    }
