    public async override Task<object> fetchOHLCV(object symbol, object timeframe = null, object since = null, object limit = null, object parameters = null)
    {
        /**
        * @method
        * @name ompfinex#fetchOHLCV
        * @description fetches historical candlestick data containing the open, high, low, and close price, and the volume of a market
        * @see https://docs.ompfinex.com/?shell#ohlc
        * @param {string} symbol unified symbol of the market to fetch OHLCV data for
        * @param {string} timeframe the length of time each candle represents
        * @param {int} [since] timestamp in ms of the earliest candle to fetch
        * @param {int} [limit] the maximum amount of candles to fetch
        * @param {object} [params] extra parameters specific to the exchange API endpoint
        * @returns {int[][]} A list of candles ordered as timestamp, open, high, low, close, volume
        */
        timeframe ??= "1h";
        parameters ??= new Dictionary<string, object>();
        await this.loadMarkets();
        object market = this.market(symbol);
        if (isTrue(isEqual(getValue(market, "quote"), "IRT")))
        {
            symbol = add(getValue(market, "base"), "IRR");
        }
        object endTime = (new DateTimeOffset(DateTime.UtcNow)).ToUnixTimeMilliseconds();
        object request = new Dictionary<string, object>() {
            { "symbol", ((string)symbol).Replace((string)"/", (string)"") },
            { "from", subtract((divide(endTime, 1000)), (multiply(multiply(24, 60), 60))) },
            { "to", divide(endTime, 1000) },
            { "resolution", this.safeString(this.timeframes, timeframe, timeframe) },
        };
        if (isTrue(!isEqual(since, null)))
        {
            ((IDictionary<string,object>)request)["from"] = divide(since, 1000);
        }
        ((IDictionary<string,object>)request)["from"] = this.safeInteger(request, "from");
        ((IDictionary<string,object>)request)["to"] = this.safeInteger(request, "to");
        if (isTrue(!isEqual(timeframe, null)))
        {
            ((IDictionary<string,object>)request)["resolution"] = this.safeString(this.timeframes, timeframe, timeframe);
        }
        object response = await this.publicGetV2UdfRealHistory(request);
        object openList = this.safeValue(response, "o", new List<object>() {});
        object highList = this.safeList(response, "h", new List<object>() {});
        object lastList = this.safeList(response, "l", new List<object>() {});
        object closeList = this.safeList(response, "c", new List<object>() {});
        object volumeList = this.safeList(response, "v", new List<object>() {});
        object timestampList = this.safeList(response, "t", new List<object>() {});
        object ohlcvs = new List<object>() {};
        for (object i = 0; isLessThan(i, getArrayLength(openList)); postFixIncrement(ref i))
        {
            if (isTrue(isEqual(getValue(market, "quote"), "IRT")))
            {
                ((List<object>)openList)[Convert.ToInt32(i)] = ((bool) isTrue(getValue(openList, i))) ? divide(getValue(openList, i), 10) : 0;
                ((List<object>)highList)[Convert.ToInt32(i)] = ((bool) isTrue(getValue(highList, i))) ? divide(getValue(highList, i), 10) : 0;
                ((List<object>)lastList)[Convert.ToInt32(i)] = ((bool) isTrue(getValue(lastList, i))) ? divide(getValue(lastList, i), 10) : 0;
                ((List<object>)closeList)[Convert.ToInt32(i)] = ((bool) isTrue(getValue(closeList, i))) ? divide(getValue(closeList, i), 10) : 0;
                ((List<object>)volumeList)[Convert.ToInt32(i)] = ((bool) isTrue(getValue(volumeList, i))) ? divide(getValue(volumeList, i), 10) : 0;
            }
            ((IList<object>)ohlcvs).Add(new List<object>() {getValue(timestampList, i), getValue(openList, i), getValue(highList, i), getValue(lastList, i), getValue(closeList, i), getValue(volumeList, i)});
        }
        return this.parseOHLCVs(ohlcvs, market, timeframe, since, limit);
