    public async override Task<object> fetchOrderBook(object symbol, object limit = null, object parameters = null)
    {
        /**
        * @method
        * @name ompfinex#fetchOrderBooks
        * @description fetches information on open orders with bid (buy) and ask (sell) prices, volumes and other data for multiple markets
        * @see https://docs.ompfinex.com/?shell#f2150d7710
        * @param {string[]|undefined} symbols list of unified market symbols, all symbols fetched if undefined, default is undefined
        * @param {int} [limit] max number of entries per orderbook to return, default is undefined
        * @param {object} [params] extra parameters specific to the exchange API endpoint
        * @returns {object} a dictionary of [order book structures]{@link https://docs.ccxt.com/#/?id=order-book-structure} indexed by market symbol
        */
        parameters ??= new Dictionary<string, object>();
        await this.loadMarkets();
        object market = this.market(symbol);
        object response = await this.publicGetV1Orderbook();
        object orderbook = this.safeDict(response, "data");
        if (isTrue(isEqual(getValue(market, "quote"), "IRT")))
        {
            orderbook = this.safeDict(orderbook, add(getValue(market, "base"), "IRR"));
            object bids = this.safeList(orderbook, "bids");
            object asks = this.safeList(orderbook, "asks");
            for (object i = 0; isLessThan(i, getArrayLength(bids)); postFixIncrement(ref i))
            {
                ((List<object>)getValue(bids, i))[Convert.ToInt32(0)] = ((bool) isTrue(getValue(getValue(bids, i), 0))) ? divide(getValue(getValue(bids, i), 0), 10) : 0;
            }
            for (object i = 0; isLessThan(i, getArrayLength(asks)); postFixIncrement(ref i))
            {
                ((List<object>)getValue(asks, i))[Convert.ToInt32(0)] = ((bool) isTrue(getValue(getValue(asks, i), 0))) ? divide(getValue(getValue(asks, i), 0), 10) : 0;
            }
            ((IDictionary<string,object>)orderbook)["bids"] = asks;
            ((IDictionary<string,object>)orderbook)["asks"] = bids;
        } else
        {
            orderbook = this.safeDict(orderbook, add(getValue(market, "base"), getValue(market, "quote")));
        }
        object timestamp = (new DateTimeOffset(DateTime.UtcNow)).ToUnixTimeMilliseconds();
        return this.parseOrderBook(orderbook, symbol, timestamp, "bids", "asks", "price", "amount");
    }

    public override object sign(object path, object api = null, object method = null, object parameters = null, object headers = null, object body = null)
    {
