    public function fetch_markets($params = array ()): array {
        /**
         * retrieves $data on all markets for bitbarg
         * @see https://api.bitbarg.com/api/v1/currencies?pageSize=-1
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {array[]} an array of objects representing $market $data
         */
        $request = array(
            'pageSize' => -1,
        );
        $response = $this->publicGetApiV1Currencies ($this->extend($request, $params));
        $result = array();
        $data = $this->safe_dict($response, 'result', array());
        $items = $this->safe_list($data, 'items', array());
        // Create markets for each currency with IRT quote
        for ($i = 0; $i < count($items); $i++) {
            $market = $this->parse_market($items[$i]);
            $result[] = $market;
        }
        return $result;
    }

    public function fetch_tickers(?array $symbols = null, $params = array ()): array {
        /**
         * fetches price tickers for multiple markets, statistical information calculated over the past 24 hours for each market
         * @see https://api.bitbarg.com/api/v1/currencies?pageSize=-1
         * @param {string[]|null} $symbols unified $symbols of the markets to fetch the $ticker for, all market tickers are returned if not assigned
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {array} a dictionary of ~@link https://docs.ccxt.com/#/?id=$ticker-structure $ticker structures~
         */
        $this->load_markets();
        if ($symbols !== null) {
            $symbols = $this->market_symbols($symbols);
        }
        $request = array(
            'pageSize' => -1,
        );
        $response = $this->publicGetApiV1Currencies ($this->extend($request, $params));
        $data = $this->safe_dict($response, 'result', array());
        $items = $this->safe_list($data, 'items', array());
        $result = array();
        for ($i = 0; $i < count($items); $i++) {
            $coinData = $items[$i];
            $baseId = $this->safe_string($coinData, 'coin');
            // Create $ticker for USDT $quote
            $quoteId = 'USDT';
            $base = $this->safe_currency_code($baseId);
            $quote = $this->safe_currency_code($quoteId);
            $symbol = $base . '/' . $quote;
            $ticker = $this->extend(array(
                'symbol' => $symbol,
                'baseId' => $baseId,
                'quoteId' => $quoteId,
                'id' => $baseId . '/' . $quoteId,
            ), $coinData);
            $result[$symbol] = $this->parse_ticker($ticker);
        }
        return $this->filter_by_array_tickers($result, 'symbol', $symbols);
    }

    public function fetch_ticker(string $symbol, $params = array ()): array {
        /**
         * fetches a price $ticker, a statistical calculation with the information calculated over the past 24 hours for a specific market
         * @see https://api.bitbarg.com/api/v1/currencies?pageSize=-1
