    public function fetch_markets($params = array ()): array {
        /**
         * retrieves data on all $markets for bitunix
         * @see https://api-docs.bitunix.ir/#be8d9c51a2
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {array[]} an array of objects representing $market data
         */
        $response = $this->publicGetApiSpotV1CommonCoinPairList ($params);
        $markets = $this->safe_list($response, 'data');
        $result = array();
        for ($i = 0; $i < count($markets); $i++) {
            $market = $this->parse_market($markets[$i]);
            $result[] = $market;
        }
        return $result;
    }

